import { useContext, useState } from "react";
import { GlobalContext } from "../context/GlobalState";
import {
  Checkbox,
  Container,
  Group,
  Title,
  Popover,
  Button,
  Text,
  Textarea
} from "@mantine/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

const TodoItem = (todo) => {
  const [completed, setCompleted] = useState(false);
  const { deleteTodo } = useContext(GlobalContext);
  const [opened, setOpened] = useState(false);

  const handleDelete = (id) => {
    console.log(id);
    deleteTodo(id);
  };

  const handleCompleted = (checked, id) => {
    setCompleted(checked);
    // completeTodo(id, completed);
  };

  return (
    <Container
      styles={(theme) => ({
        root: {
          width: "100%",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          borderBottom: "1px solid #f3c2c2",
          padding: "10px"
        }
      })}
    >
      <Group>
        <Checkbox
          onChange={(e) => handleCompleted(!e.target.checked, todo.id)}
        ></Checkbox>
        <Title order={5}>{todo.text}</Title>
      </Group>
      <Popover
        opened={opened}
        onClose={() => setOpened(false)}
        target={<Button onClick={() => setOpened((o) => !o)}>Edit</Button>}
        width={260}
        position="bottom"
        withArrow
      >
        <div style={{ display: "flex" }}>
          <Text size="sm">
            <Textarea label="Edit to do" value={todo.text}></Textarea>
            <Button>Save</Button>
            <Button>Cancel</Button>
          </Text>
        </div>
      </Popover>

      <a style={{ cursor: "pointer" }} onClick={() => handleDelete(todo.id)}>
        <Button>Delete</Button>
        {/* <FontAwesomeIcon style={{ fontSize: "20px", color: "#474747" }} icon={faTrash} /> */}
      </a>
    </Container>
  );
};

export default TodoItem;
