import React, { useContext, useEffect, useState } from "react";
import { Button, Loader, Select, TextInput } from "@mantine/core";
import { GlobalContext } from "../context/GlobalState";

const NewTodoInput = () => {
  const { addTodo, getFolders, folders, isLoading } = useContext(GlobalContext);
  const [todo, setTodo] = useState("");
  const [folder, setFolder] = useState("");

  useEffect(() => {
    getFolders();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const data = folders?.map((folder) => folder.name);

  const submitTodo = (e) => {
    e.preventDefault();
    const newTodo = {
      text: todo,
      completed: false,
      folder
    };

    addTodo(newTodo);

    setTodo("");
  };

  return (
    <div>
      {!isLoading ? (
        <form onSubmit={submitTodo} className="input">
          <TextInput
            label="Add a Todo"
            variant="filled"
            placeholder="Take out the trash"
            value={todo}
            onChange={(e) => {
              setTodo(e.currentTarget.value);
            }}
          />
          <Select
            data={data}
            placeholder="Select a folder"
            nothingFound="Nothing found"
            clearable
            error

          />
          <Button
            variant="light"
            color="teal"
            type="submit"
            size="sm"
            styles={(theme) => ({
              root: {
                width: 45,
                padding: 0,
                alignSelf: "flex-end"
              }
            })}
            {...(!todo && { disabled: true })}
          >
            +
          </Button>
        </form>
      ) : (
        <Loader color="gray" variant="bars" size="md" />
      )}
    </div>
  );
};

export default NewTodoInput;
