import { useContext, useEffect } from 'react';
import { GlobalContext } from "../context/GlobalState"
import { Accordion, Group, Loader, ScrollArea } from '@mantine/core';
import TodoItem from './TodoItem';
import FolderItem from './FolderItem';

const FolderList = () => {
  const { getFolders, folders, isLoading} = useContext(GlobalContext);

  useEffect(() => {
    getFolders();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
    <Group spacing="xs" position="center" styles={(theme) => ({
      root: {
        overflow: "auto",
        height: "100%"
      }
    })}>
      {!isLoading ?
          <Accordion spacing="xs" styles={(theme) => ({
            root: {
              width: "100%",
            }
          })}>
            {folders.map((folder) => (
              <FolderItem key={folder.name} {...folder} />
            ))}
          </Accordion>
        : <Loader color="gray" variant="bars" size="md" />
      }
    </Group>
  );

}

export default FolderList;
